import React from 'react';
import SearchForm from './SearchForm';
import SearchResults from './SearchResults';
import BasicPagination from './BasicPagination'
import axios from 'axios';
import CssBaseline from '@material-ui/core/CssBaseline';
import Typography from '@material-ui/core/Typography';
import Container from '@material-ui/core/Container';
import Box from '@material-ui/core/Box';

class App extends React.Component {

    state = {
        searchResults: [],
        page: 1,
        term: '',
    };

    // onPageSubmit = async (url, page) => {
    //     const result = await axios.get(`${url}&page=${page}`, {
    //         auth: {
    //             username: 'ryanyang-pg',
    //             password: '31eae963b5a63fa8cea3e79cdcf7fb7e4fbc31e7'
    //         }
    //     });

    //     if (result.data) {
    //         this.setState({
    //             searchResults: result.data,
    //         })
    //         // result.data.items.map(async (element) => {
    //         //     const languagesResponse = await axios.get(element['languages_url']);
    //         //     console.log(languagesResponse.data);
    //         //     return;
    //         // });
    //     }
    // }

    onSearchSubmit = async (term, page) => {
        const result = await axios.get('https://api.github.com/search/repositories', {
            params: { 
                q: term,
                page: page,
            },
            // auth: {
            //     username: 'ryanyang-pg',
            //     password: '31eae963b5a63fa8cea3e79cdcf7fb7e4fbc31e7'
            // }
        });

        this.setState({
            term: term,
            page: page,
        });

        console.log();

        if (result.data) {
            this.setState({
                searchResults: result.data,
            });
        }
    }
    render() {
        return (
            <Container component="main" maxWidth="sm">
                <CssBaseline />
                <SearchForm onSubmit={this.onSearchSubmit} />
                <SearchResults searchResults={this.state.searchResults} />
                <BasicPagination page={this.state.page} url={this.state.term} onSubmit={this.onSearchSubmit} />
                <Box m={2}>
                    <Typography p="20" variant="body2" color="textSecondary" align="center">
                    {'Ryan Yang.  Booqed Challenge. '}{new Date().getFullYear()}{'.'}
                    </Typography>
                </Box>
            </Container>
        )
    }
}

export default App;
