import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import Link from '@material-ui/core/Link';
import StarIcon from '@material-ui/icons/Star';
import CallSplitIcon from '@material-ui/icons/CallSplit';
import Avatar from '@material-ui/core/Avatar';
import TimeAgo from 'javascript-time-ago'
import en from 'javascript-time-ago/locale/en'

import Box from '@material-ui/core/Box';

const useStyles = makeStyles({
  root: {
    minWidth: 275,
    marginBottom: 20,
  },
  bullet: {
    display: 'inline-block',
    margin: '0 2px',
    transform: 'scale(0.8)',
  },
  title: {
    fontSize: 14,
  },
  pos: {
    marginBottom: 12,
  },
  searchTotal: {    
    paddingBottom: '15px',   
  },
  githubAvatar: {
      width: 100,
      height: 100,
  },
  githubCounters: {
      marginTop: 5,
  }
});

const SearchResults = (props) => {

    const classes = useStyles();
    const bull = <span className={classes.bullet}>â€¢</span>;

    const results = props.searchResults;
    // console.log(results['total_count']);
    // console.log(results['incomplete_results']);

    if (results['items']) {
        TimeAgo.addLocale(en)
        const timeAgo = new TimeAgo('en-US')
        const stuff = results['items'].map(element => {
            const ago = timeAgo.format(new Date(element['updated_at']));

            return (
                <Card className={classes.root}>
                    <CardContent>
                        <Avatar alt="Remy Sharp" src={element['owner']['avatar_url']} className={classes.githubAvatar} />
                        <Typography className={classes.title} color="textSecondary" gutterBottom>
                        {ago}
                        </Typography>
                        <Typography variant="h5" component="h2">
                            <Link href={element['html_url']}>
                                {element['full_name']}
                            </Link>
                        </Typography>
                        <Typography className={classes.pos} color="textSecondary">
                        <span>{element['stargazers_count']} <StarIcon fontSize="small" className={classes.githubCounters} /></span>
                        <span>{element['forks_count']} <CallSplitIcon fontSize="small" className={classes.githubCounters} /></span>
                        </Typography>

                        <Typography variant="body2" component="p">
                        {element['description']}
                        <br />
                        {'"a benevolent smile"'}
                        </Typography>
                    </CardContent>
                    <CardActions>
                        <Button size="small">Learn More</Button>
                    </CardActions>
                </Card>
            )
        });

        return (
            <div>
                <Typography className={classes.searchTotal}>
                    {results['total_count']} repositories found.
                </Typography>
                <box m={2}>
                    {stuff}
                </box>

            </div>
        );
    }
    // console.log();
    // for (var i=0;i<results['items'].length; i++) {
    //     console.log(results['items'][i]);
    // }
    // console.log(results['items']);
    // console.log(results.items);
    // const stuff = results['items'].map((row) => {
    //     console.log(row);
    //     return null;
    // });

    return null;
};

export default SearchResults;
