  handleChange = (event, value) => {
    event.preventDefault();
    console.log(this.props);
    this.props.onSubmit(this.props.term, value);
  }

  render() {
    console.log(this.props);
    const { classes } = this.props;
    const pageCount = (this.props.totalPages < 10)? this.props.totalPages: 10

    return (
      <div className={classes.root}>
        <Pagination count={pageCount} page={this.props.page} onChange={this.handleChange} />
      </div>
    );
    }
}
export default withStyles(useStyles)(BasicPagination);
