import React from 'react';
import { withStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import Link from '@material-ui/core/Link';
import StarIcon from '@material-ui/icons/Star';
import CallSplitIcon from '@material-ui/icons/CallSplit';
import Avatar from '@material-ui/core/Avatar';
import TimeAgo from 'javascript-time-ago'
import en from 'javascript-time-ago/locale/en'
import CircularProgress from '@material-ui/core/CircularProgress';

const useStyles = theme => ({
  root: {
    position: 'relative',
    minWidth: 275,
    marginBottom: 20,
  },
  bullet: {
    display: 'inline-block',
    margin: '0 2px',
    transform: 'scale(0.8)',
  },
  title: {
    fontSize: 14,
  },
  pos: {
    marginTop: 7,
    marginBottom: 12,
    backgroundColor: '#dc004e',
    color: '#fff',
    display: 'inline-block',
    borderRadius: 7,
  },
  searchTotal: {    
    paddingBottom: '15px',   
  },
  githubAvatar: {
      width: 100,
      height: 100,
      position: 'absolute',
      top: 30,
      left: 20,
  },
  githubIcons: {
      top: 5,
      position: 'relative',
      left: 0,
      marginLeft: 5,
  },
  githubCounters: {
      padding: 2,
      borderRadius: 5,
      fontSize: '0.9rem',
      marginRight: 5,
  },
  githubCard: {
      paddingLeft: 140,
  },
  searchResultsContainer: {
      position: 'relative',
      width: '100%',
      height: '100%',
  },
  loading: {
      position: 'absolute',
      backgroundColor: 'black',
      top: 0,
      left: 0,
      zIndex: 999,
  }
});

class SearchResults extends React.Component {

    // constructor(props) {
    //     super(props);
    //     // this.state
    // }

    render() {
        const { classes } = this.props;

        // const classes = useStyles();
        // const bull = <span className={classes.bullet}>â€¢</span>;

        const results = this.props.searchResults;
        // console.log(results['total_count']);
        // console.log(results['incomplete_results']);

        //<CircularProgress />
        const loading = (this.props.loading) ? <div width="100%" height="100%" className={classes.loading}>uhuhhuhu</div>: null;

        if (results['items']) {
            TimeAgo.addLocale(en)
            const timeAgo = new TimeAgo('en-US')
            const stuff = results['items'].map(element => {
                const ago = timeAgo.format(new Date(element['updated_at']));

                return (
                    <Card className={classes.root}>
                        <CardContent className={classes.githubCard}>
                            <Avatar alt="Remy Sharp" src={element['owner']['avatar_url']} className={classes.githubAvatar} />
                            <Typography className={classes.title} color="textSecondary" gutterBottom>
                            Last updated {ago}
                            </Typography>
                            <Typography variant="h5" component="h2">
                                <Link href={element['html_url']} target="_blank">
                                    {element['full_name']}
                                </Link>
                            </Typography>
                            <Typography className={classes.pos} color="textSecondary">
                                <StarIcon fontSize="small" className={classes.githubIcons} />
                                <span className={classes.githubCounters}>{element['stargazers_count']}</span>
                                <CallSplitIcon fontSize="small" className={classes.githubIcons} />
                                <span className={classes.githubCounters}>{element['forks_count']}</span>
                            </Typography>

                            <Typography variant="body2" component="p">
                            {element['description']}
                            <br />
                            {'"a benevolent smile"'}
                            </Typography>
                        </CardContent>
                        <CardActions>
                            <Button size="small">Learn More</Button>
                        </CardActions>
                    </Card>
                )
            });

            return (
                <div>
                    <Typography className={classes.searchTotal}>
                        {results['total_count']} repositories found.
                    </Typography>
                    <box m={2} className={classes.searchResultsContainer}>
                        {loading}
                        {stuff}
                    </box>

                </div>
            );
        }

        return null;
    }
}

export default withStyles(useStyles)(SearchResults);
