import React from 'react';
import SearchForm from './SearchForm';
import SearchResults from './SearchResults';
import axios from 'axios';
import CssBaseline from '@material-ui/core/CssBaseline';
import Typography from '@material-ui/core/Typography';
import Container from '@material-ui/core/Container';
import Box from '@material-ui/core/Box';

class App extends React.Component {

    state = {
        searchResults: [],
    };

    onSearchSubmit = async (term) => {
        const result = await axios.get('https://api.github.com/search/repositories', {
            params: { q: term },
            auth: {
                username: 'ryanyang-pg',
                password: '31eae963b5a63fa8cea3e79cdcf7fb7e4fbc31e7'
            }
        });

        if (result.data) {
            this.setState({
                searchResults: result.data,
            })
            result.data.items.map(async (element) => {
                const languagesResponse = await axios.get(element['languages_url']);
                console.log(languagesResponse.data);
                return;
            });
        }
    }
    render() {
        return (
            <Container component="main" maxWidth="sm">
                <CssBaseline />
                <SearchForm onSubmit={this.onSearchSubmit} />
                <SearchResults searchResults={this.state.searchResults} />
                <Box m={2}>
                    <Typography p="20" variant="body2" color="textSecondary" align="center">
                    {'Ryan Yang.  Booqed Challenge. '}{new Date().getFullYear()}{'.'}
                    </Typography>
                </Box>
            </Container>
        )
    }
}

export default App;
